from pybricks.hubs import PrimeHub
from pybricks.parameters import Direction, Port, Stop
from pybricks.pupdevices import Motor
from pybricks.robotics import DriveBase

# Set up all devices.
prime_hub = PrimeHub()
motor = Motor(Port.E, Direction.COUNTERCLOCKWISE)
motor_2 = Motor(Port.F, Direction.CLOCKWISE)
motor_3 = Motor(Port.D, Direction.CLOCKWISE)
drive_base = DriveBase(motor, motor_2, 56, 85)


# The main program starts here.
# roof and scale good the ring pull is needing some work
# Freeze 1/2/2026
drive_base.settings(straight_speed=750)
drive_base.settings(turn_rate=750)
drive_base.settings(straight_acceleration=500)
drive_base.settings(turn_acceleration=500)
drive_base.use_gyro(True)
motor_3.run_until_stalled(500, Stop.HOLD, 20)
motor.reset_angle(0)
drive_base.straight(330)
drive_base.turn(-90)
drive_base.straight(660)
drive_base.turn(90)
drive_base.straight(270)
drive_base.turn(90)
motor_3.run_angle(700, -70)
motor_3.run_angle(500, 70)
drive_base.turn(-45)
drive_base.straight(285)
drive_base.turn(85)
drive_base.straight(220)
drive_base.straight(-220)
drive_base.turn(90)
drive_base.straight(500)
drive_base.turn(-70)
