from pybricks.hubs import PrimeHub
from pybricks.parameters import Direction, Port, Stop
from pybricks.pupdevices import Motor
from pybricks.robotics import DriveBase
from pybricks.tools import multitask, run_task, wait

# Set up all devices.
prime_hub = PrimeHub()
motor_2 = Motor(Port.E, Direction.COUNTERCLOCKWISE)
motor_2_2 = Motor(Port.F, Direction.CLOCKWISE)
motor = Motor(Port.D, Direction.CLOCKWISE)
drive_base_ = DriveBase(motor_2, motor_2_2, 56, 85)

async def subtask():
    await motor.run_until_stalled(1000, Stop.COAST, 50)
    motor.reset_angle(0)
    await motor.run_angle(500, -110)

async def subtask2():
    await drive_base_.straight(650)
    await drive_base_.turn(-86)

async def subtask3():
    await drive_base_.straight(50)
    await drive_base_.turn(100)
    await drive_base_.straight(415)

async def subtask4():
    await motor.run_until_stalled(1000, Stop.HOLD, 30)
    motor.reset_angle(0)
    await motor.run_target(1000, -130)

async def subtask5():
    await motor.run_until_stalled(1000, Stop.HOLD, 30)
    motor.reset_angle(0)
    await motor.run_target(1000, -100)

async def subtask6():
    await drive_base_.straight(335)
    await drive_base_.turn(50)

async def main():
    # JAJ - M1,M2, & M4
    # 1/3/2026 5:56 pm
    drive_base_.use_gyro(True)
    drive_base_.settings(turn_rate=140)
    drive_base_.settings(straight_speed=350)
    await drive_base_.straight(-50)
    await multitask(
        subtask(),
        subtask2(),
    )
    await drive_base_.turn(-45)
    await drive_base_.turn(45)
    await wait(500)
    await drive_base_.turn(80)
    await multitask(
        subtask3(),
        subtask4(),
    )
    await drive_base_.turn(-95)
    await drive_base_.straight(10)
    await motor.run_target(100, -40)
    await drive_base_.straight(30)
    await motor.run_until_stalled(30, Stop.COAST, 50)
    await wait(1000)
    await motor.run_angle(500, -100)
    drive_base_.settings(straight_speed=400)
    motor.control.limits(acceleration=2000)
    await drive_base_.straight(-30)
    await drive_base_.turn(-90)
    await multitask(
        subtask5(),
        subtask6(),
    )
    drive_base_.settings(straight_speed=150)
    await wait(500)
    drive_base_.use_gyro(False)
    await drive_base_.straight(110)
    await motor.run_angle(500, 90)
    await drive_base_.turn(90)
    await motor.run_angle(500, -90)
    await drive_base_.straight(-30)
    await drive_base_.turn(-70)
    await motor.run_angle(500, -70)
    await drive_base_.straight(80)
    await motor.run_angle(500, 65)
    await drive_base_.straight(-80)
    await drive_base_.turn(-35)
    await drive_base_.straight(170)


run_task(main())