from pybricks.hubs import PrimeHub
from pybricks.parameters import Direction, Port, Stop
from pybricks.pupdevices import Motor
from pybricks.robotics import DriveBase

# Set up all devices.
prime_hub = PrimeHub()
motor_3 = Motor(Port.E, Direction.COUNTERCLOCKWISE)
motor_2 = Motor(Port.F, Direction.CLOCKWISE)
motor = Motor(Port.D, Direction.COUNTERCLOCKWISE)
drive_base = DriveBase(motor_3, motor_2, 56, 85)


# The main program starts here.
# JCB mission 5 and mission 6
# 1/3/26 -10:45 upd
drive_base.use_gyro(True)
motor.run_until_stalled(500, Stop.COAST, 30)
motor.reset_angle(0)
motor.run_target(500, -100)
drive_base.straight(733)
motor.run_target(500, -85)
drive_base.turn(-33)
drive_base.straight(35)
drive_base.turn(-20)
drive_base.turn(-120)
drive_base.straight(320)
drive_base.turn(-30)
drive_base.straight(330)
